import React from 'react';
import ItemPicker from './item-picker'

function Item(props) {
  console.log(props)
  return (
    <div className="">
      <div>{props.shopitem.Name}</div>
       Item Gallery
       <div>{props.shopitem.Description}</div>
       <div>{formatPrice(props.shopitem.Price)}</div>
       <ItemPicker/>
    </div>
  );
}

function formatPrice(price){
  return "$"+price*.01;
}

export default Item;
