import React, {Component} from 'react';
import Item from './components/Item'
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state = {
      shopItems: {}
    }
  }
  componentDidMount() {
    fetch("https://swapi.co/api/people/1")
    .then(response => response.json())
    .then(data => {
      this.setState({
        shopItems: data
      })
    })
  }

  render(){
    return (
      <div className="App">
        <header className="">
          <div>Bad Magic</div>
          <div>this.state.shopItems.name</div>
        </header>
        <Item/>


      </div>
    );
  }
}

export default App
