import React from 'react';

const bucketURL = "https://badmagic-shopitems.s3.amazonaws.com/"

function ItemGallery(props) {
    var flipped=false
  return (
    <div className="" onMouseOver={this.flipped=true} onMouseOut={this.flipped=false}>
       <img className="" src={imageURL(props.options)}/>
       <img className="" src={imageURLFlip(props.options)}/>
    </div>
  );
}

function addItemToCart(){
  console.log("Adding to Cart")
}

function imageURL(options){
    return bucketURL+options+"/"+options+"-1.jpg"
}
function imageURLFlip(options){
    return bucketURL+options+"/"+options+"-2.jpg"
}
export default ItemGallery;
