import React, {Component} from 'react';
import Item from './components/Item'
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state = {
      shopItems: []
    }
  }
  componentDidMount() {
    fetch("https://swapi.co/api/people")
    .then(response => response.json())
    .then(data => {
      console.log(data)
      this.setState({
        shopItems: data.results.map(item => <Item shopitem={item}/>)
      })
      console.log(this.state.shopItems)
    })
  }

  render(){
    return (
      <div className="App">
        <header className="">
          <div>Bad Magic</div>
          <div></div>
        </header>
        {this.shopItems}


      </div>
    );
  }
}

export default App
